mapper.select:
        SELECT USER_SN
		FROM TB_USER
		WHERE USER_ID = #{id}

mapper.insert:
    	INSERT INTO TB_USER(
			USER_SN 
		  , USER_ID 
		  , USER_PSWRD 
		  , USER_TYPE 
		  , USER_NM 
		  , USER_GNDR
		  , USER_BRT 
		  , USER_ADR 
		  , USER_PN
		  , RGSTR_DT
		) 
		SELECT 
			IFNULL(MAX_USER_SN, 0) + 1
		  , #{param[id]}
		  , #{param[password]}
		  , 0
		  , #{param[name]}
		  , #{param[gender]}
		  , #{param[birthday]}
		  , #{param[addressDetail]}
		  , #{param[phone]}
		  , sysdate()
		FROM (
		    SELECT MAX(USER_SN) AS MAX_USER_SN FROM TB_USER
		) AS subquery
